function isInViewport(t){var i=t.getBoundingClientRect();return(i.height>0||i.width>0)&&i.bottom>=0&&i.right>=0&&i.top<=(window.innerHeight||document.documentElement.clientHeight)&&i.left<=(window.innerWidth||document.documentElement.clientWidth)}$(document).ready(function(){$(".parallax").each(function(t){var i=$(this).data("image-src"),n=$(this).data("height");$(this).css("background-image","url("+i+")"),$(this).css("height",n)}),$(window).scroll(function(){var t=$(window).scrollTop();$(".parallax").each(function(i,n){var e=$(this).offset().top-450,o=$(this).height();$(this).height();if(isInViewport(this)){var h=t-e,a=Math.round(h/o*100);$(this).css("background-position","center "+parseInt(-1.5*a)+"px")}})})});